contracts/abstract/Compatibility.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

abstract contract Compatibility {
    event Received(address sender, uint256 amount);

    receive() external payable {
        emit Received(msg.sender, msg.value);
    }

    function onERC721Received(address, address, uint256, bytes calldata) external pure returns (bytes4) {
        return this.onERC721Received.selector;
    }

    function onERC1155Received(address, address, uint256, uint256, bytes calldata) external pure returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(address, address, uint256[] calldata, uint256[] calldata, bytes calldata)
        external
        pure
        returns (bytes4)
    {
        return this.onERC1155BatchReceived.selector;
    }
}

contracts/account/BondSmartAccount.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@account-abstraction/contracts/legacy/v06/IEntryPoint06.sol";
import "@account-abstraction/contracts/legacy/v06/IAccount06.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/proxy/utils/UUPSUpgradeable.sol";
import {IBondProtocol} from "../interfaces/IBondProtocol.sol";
import {IBondPool} from "../interfaces/IBondPool.sol";
import {DataTypes} from "../libraries/DataTypes.sol";
import {Compatibility} from "../abstract/Compatibility.sol";
import {IERC20} from "../interfaces/IERC20.sol";

// The implementation contract for the smart account
contract BondSmartAccount is
    IAccount06,
    Compatibility,
    UUPSUpgradeable,
    Initializable
{
    address public owner;
    IEntryPoint public immutable entryPoint;
    IBondProtocol public protocol;

    uint256 private constant SIG_VALIDATION_FAILED = 1;
    uint256 private constant SIG_VALIDATION_SUCCESS = 0;

    bytes4 private constant EXECUTE_INTENT_SELECTOR =
        bytes4(keccak256("executeIntent(bytes32,address)"));

    event BondAccountInitialized(
        IEntryPoint indexed entryPoint,
        address indexed owner
    );
    event IntentExecuted(bytes32 indexed intentId, address indexed executor);

    error OnlyOwner();
    error OnlyEntryPoint();
    error TransactionExecutionFailed();
    error InvalidIntentSender();
    error IntentSrcNotSettled();
    error IntentExpired();
    error InvalidIntent();
    error InvalidIntentExecuted();
    error InvalidExecutionChain();
    error FailedToSettleExecutor();
    error InsufficientFunds();

    // EntryPoint is set once during implementation deployment
    constructor(IEntryPoint _entryPoint, IBondProtocol _protocol) {
        entryPoint = _entryPoint;
        protocol = _protocol;
        _disableInitializers();
    }

    function initialize(address anOwner) public virtual initializer {
        _initialize(anOwner);
    }

    function _initialize(address anOwner) internal virtual {
        owner = anOwner;
        emit BondAccountInitialized(entryPoint, owner);
    }

    modifier onlyOwner() {
        if (msg.sender != owner && msg.sender != address(this))
            revert OnlyOwner();
        _;
    }

    modifier onlyEntryPoint() {
        if (msg.sender != address(entryPoint)) revert OnlyEntryPoint();
        _;
    }

    // Execute function for direct calls from owner
    function execute(
        address target,
        uint256 value,
        bytes calldata data
    ) external onlyEntryPoint returns (bytes memory) {
        (bool success, bytes memory result) = target.call{value: value}(data);
        if (!success) revert TransactionExecutionFailed();

        return result;
    }

    // Batch execute function for gas efficiency
    function executeBatch(
        address[] calldata targets,
        uint256[] calldata values,
        bytes[] calldata datas
    ) external onlyEntryPoint returns (bytes[] memory results) {
        require(
            targets.length == values.length && targets.length == datas.length,
            "Array length mismatch"
        );

        results = new bytes[](targets.length);
        for (uint256 i = 0; i < targets.length; i++) {
            (bool success, bytes memory result) = targets[i].call{
                value: values[i]
            }(datas[i]);
            require(success, "Transaction execution failed");
            results[i] = result;
        }
    }

    // Execute function for direct calls from owner
    function executeIntent(
        bytes32 _intentId,
        address _executor
    ) external onlyEntryPoint returns (bytes[] memory results) {
        DataTypes.IntentData memory _intent = protocol.getIntent(_intentId);
        // make sure the acoount is the owner of intent
        if (_intent.sender != address(this)) revert InvalidIntentSender();
        // check if intent is expired
        if (_intent.expires < block.timestamp) revert IntentExpired();
        // check if current chain is the intent destination chain
        if (_intent.dstChainId == block.chainid) {
            bool dstChainSettled = protocol.isIntentDstChainFullySettled(
                _intentId
            );
            if (!dstChainSettled) revert IntentSrcNotSettled();
            // if infact the destination chain has been settled
            // check if intent has been used to execute a transaction
            if (protocol.isIntentExecuted(_intentId))
                revert InvalidIntentExecuted();
            // decode intent destination datas
            DataTypes.IntentDstData[] memory _dstDatas = abi.decode(
                _intent.dstDatas,
                (DataTypes.IntentDstData[])
            );
            // declare results length
            results = new bytes[](_dstDatas.length);
            for (uint256 i = 0; i < _dstDatas.length; i++) {
                (bool success, bytes memory result) = _dstDatas[i].target.call{
                    value: _dstDatas[i].value
                }(_dstDatas[i].data);
                if (!success) revert TransactionExecutionFailed();
                results[i] = result;
            }

            (bool executeSuccess, ) = address(protocol).call(
                abi.encodeWithSelector(
                    IBondProtocol.settleIntentDestExecutor.selector,
                    _intentId,
                    _executor
                )
            );
            if (!executeSuccess) revert FailedToSettleExecutor();
        } else {
            // if it is not the destination chain then it is the source chain.
            // loop through intent source chain to find the amount to send to destination chain
            uint256 _intentSrcAmount = 0;
            for (uint i = 0; i < _intent.srcChainIds.length; ) {
                if (_intent.srcChainIds[i] == block.chainid) {
                    _intentSrcAmount = _intent.srcAmounts[i];
                }
                unchecked {
                    ++i;
                }
            }
            if (_intentSrcAmount == 0) revert InvalidExecutionChain();
            // get pool info so we can get the underlying token we interact with,
            // this is the token to interact with and send to destination chain.
            // using a single poolId across chain helps us to manage tokens on different chains
            DataTypes.PoolData memory _poolData = protocol.getPool(
                _intent.poolId
            );
            // now we have the pool data containing the token information we need to make 2 calls
            // first approve the protocol to spend the token
            // second call interacts with the protocol to bridge tokens to destination chain

            // make sure user has enough balance before calling approve
            if (
                IERC20(_poolData.underlyingToken).balanceOf(address(this)) <
                _intentSrcAmount
            ) revert InsufficientFunds();
            // approves the protocol to spend the token
            (bool approveSuccess, bytes memory successResult) = address(
                _poolData.underlyingToken
            ).call(
                    abi.encodeWithSelector(
                        IERC20.approve.selector,
                        address(protocol),
                        _intentSrcAmount
                    )
                );
            if (!approveSuccess) revert TransactionExecutionFailed();

            // call the protocol to settle destination chain
            (bool executeSuccess, bytes memory executeResult) = address(
                protocol
            ).call(
                    abi.encodeWithSelector(
                        IBondProtocol.settleIntentDestChain.selector,
                        _intentId,
                        _executor
                    )
                );
            if (!executeSuccess) revert TransactionExecutionFailed();

            results = new bytes[](2);
            results[0] = successResult;
            results[1] = executeResult;

            return results;
        }

        emit IntentExecuted(_intentId, _executor);
    }

    // Validate user operation (called by EntryPoint)
    function validateUserOp(
        UserOperation06 calldata userOp,
        bytes32 userOpHash,
        uint256
    ) external view override onlyEntryPoint returns (uint256 validationData) {
        // Check signature length to determine type
        if (userOp.signature.length == 32) {
            // Intent-based validation
            return _validateIntentSignature(userOp);
        } else {
            // Traditional ECDSA signature validation
            return _validateECDSASignature(userOpHash, userOp.signature);
        }
    }

    function _validateECDSASignature(
        bytes32 userOpHash,
        bytes calldata signature
    ) internal view returns (uint256) {
        bytes32 messageHash = MessageHashUtils.toEthSignedMessageHash(
            userOpHash
        );
        address signer = ECDSA.recover(messageHash, signature);
        if (owner != signer) return SIG_VALIDATION_FAILED;
        return SIG_VALIDATION_SUCCESS;
    }

    function _validateIntentSignature(
        UserOperation06 calldata userOp
    ) internal view returns (uint256) {
        // Extract intentId from signature (32 bytes)
        bytes32 intentId = bytes32(userOp.signature);

        // Validate intentId is not zero
        if (intentId == bytes32(0)) return SIG_VALIDATION_FAILED;

        // 1. Check if callData is calling executeIntent function
        if (userOp.callData.length < 4) return SIG_VALIDATION_FAILED;

        bytes4 functionSelector = bytes4(userOp.callData[:4]);
        if (functionSelector != EXECUTE_INTENT_SELECTOR)
            return SIG_VALIDATION_FAILED;

        // 2. Decode the executeIntent parameters to check intentId match
        if (!_validateExecuteIntentCall(userOp.callData, intentId)) {
            return SIG_VALIDATION_FAILED;
        }

        // 3. Check if intent is valid using protocol contract
        if (!protocol.isIntentValid(intentId, address(this)))
            return SIG_VALIDATION_FAILED;

        return SIG_VALIDATION_SUCCESS;
    }

    function _validateExecuteIntentCall(
        bytes calldata callData,
        bytes32 expectedIntentId
    ) internal view returns (bool) {
        // Skip the function selector (first 4 bytes) and decode parameters
        if (callData.length < 4 + 64) {
            // 4 bytes selector + minimum 64 bytes for two parameters
            return false;
        }

        // Decode the executeIntent function call
        // executeIntent(bytes memory _intentData, address _executor)
        try this._decodeExecuteIntentCall(callData[4:]) returns (
            bytes32 intentId,
            address executor
        ) {
            // Validate intentId matches and executor is not zero address
            return
                intentId == expectedIntentId &&
                intentId != bytes32(0) &&
                executor != address(0);
        } catch {
            return false;
        }
    }

    // Helper function to decode executeIntent call and extract intentId
    function _decodeExecuteIntentCall(
        bytes calldata data
    ) external pure returns (bytes32, address) {
        // Decode the parameters: (bytes32 _intentId, address _executor)
        (bytes32 intentId, address executor) = abi.decode(data, (bytes32, address));
        return (intentId, executor);
    }

    function getEntryPoint() external view returns (IEntryPoint) {
        return entryPoint;
    }

    // Add ERC165 support for interface detection
    function supportsInterface(
        bytes4 interfaceId
    ) external pure returns (bool) {
        return
            interfaceId == 0x01ffc9a7 || // ERC165
            interfaceId == type(IAccount06).interfaceId;
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal virtual override onlyOwner {
        (newImplementation);
    }
}

contracts/factory/BondAccountFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "../account/BondSmartAccount.sol";
import "@openzeppelin/contracts/utils/Create2.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";

contract BondAccountFactory {
    address public immutable implementation;
    address public immutable entryPoint;
    address public protocol;

    // Event for account creation
    event AccountCreated(
        address indexed account,
        address indexed owner,
        bytes32 salt
    );

    constructor(address _implementation, address _entryPoint, address _protocol) {
        require(
            _implementation != address(0),
            "Invalid implementation address"
        );
        require(
            _implementation.code.length > 0,
            "Implementation must be a contract"
        );
        require(_entryPoint != address(0), "Invalid entryPoint address");
        require(_protocol != address(0), "Invalid protocol address");

        implementation = _implementation;
        entryPoint = _entryPoint;
        protocol = _protocol;
    }

    function createAccount(
        address owner,
        bytes32 salt
    ) external returns (address) {
        // Calculate the expected address first
        address expectedAddress = getAccountAddress(owner, salt);

        // Check if account already exists to avoid revert
        if (expectedAddress.code.length > 0) {
            return expectedAddress;
        }

        // Generate initialization data - only pass owner since entryPoint is already in implementation
        bytes memory initData = abi.encodeWithSelector(
            BondSmartAccount.initialize.selector,
            owner
        );

        // Deploy proxy with Create2
        bytes memory proxyBytecode = abi.encodePacked(
            type(ERC1967Proxy).creationCode,
            abi.encode(implementation, initData)
        );

        address account = Create2.deploy(0, salt, proxyBytecode);

        emit AccountCreated(account, owner, salt);

        return account;
    }

    function getAccountAddress(
        address owner,
        bytes32 salt
    ) public view returns (address) {
        bytes memory initData = abi.encodeWithSelector(
            BondSmartAccount.initialize.selector,
            owner
        );

        bytes memory proxyBytecode = abi.encodePacked(
            type(ERC1967Proxy).creationCode,
            abi.encode(implementation, initData)
        );

        return
            Create2.computeAddress(
                salt,
                keccak256(proxyBytecode),
                address(this)
            );
    }

    // Helper function to generate deterministic salt
    function generateSalt(
        address owner,
        uint256 index
    ) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(owner, index));
    }

    // Get the entryPoint address used by this factory
    function getEntryPoint() external view returns (address) {
        return entryPoint;
    }

    // Get the protocol address used by this factory
    function getProtocol() external view returns (address) {
        return protocol;
    }
}

contracts/factory/ImplementationDeployer.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import {Create2} from "@openzeppelin/contracts/utils/Create2.sol";
import {BondSmartAccount} from "../account/BondSmartAccount.sol";
import {BondAccountFactory} from "./BondAccountFactory.sol";

// Helper contract for deterministic deployment of implementation and factory
contract ImplementationDeployer {
    event ImplementationDeployed(
        address indexed implementation,
        address indexed entryPoint,
        address protocol,
        bytes32 salt
    );
    event FactoryDeployed(
        address indexed factory,
        address indexed implementation,
        address protocol,
        bytes32 salt
    );

    // Deploy SmartAccount implementation with deterministic address and entryPoint
    function deployImplementation(
        address entryPoint,
        address protocol,
        bytes32 salt
    ) public returns (address) {
        require(entryPoint != address(0), "Invalid entryPoint address");

        require(protocol != address(0), "Invalid protocol address");

        address expectedAddr = getImplementationAddress(
            entryPoint,
            protocol,
            salt
        );
        if (expectedAddr.code.length > 0) {
            return expectedAddr;
        }

        bytes memory bytecode = abi.encodePacked(
            type(BondSmartAccount).creationCode,
            abi.encode(entryPoint, protocol)
        );

        address implementation = Create2.deploy(0, salt, bytecode);

        emit ImplementationDeployed(implementation, entryPoint, protocol, salt);
        return implementation;
    }

    // Get the deterministic implementation address before deployment
    function getImplementationAddress(
        address entryPoint,
        address protocol,
        bytes32 salt
    ) public view returns (address) {
        bytes memory bytecode = abi.encodePacked(
            type(BondSmartAccount).creationCode,
            abi.encode(entryPoint, protocol)
        );

        return Create2.computeAddress(salt, keccak256(bytecode), address(this));
    }

    // Deploy factory with the implementation address
    function deployFactory(
        address implementation,
        address entryPoint,
        address protocol,
        bytes32 salt
    ) public returns (address) {
        require(entryPoint != address(0), "Invalid entryPoint address");

        require(protocol != address(0), "Invalid protocol address");

        address expectedAddr = getFactoryAddress(
            implementation,
            entryPoint,
            protocol,
            salt
        );
        if (expectedAddr.code.length > 0) {
            return expectedAddr;
        }

        bytes memory bytecode = abi.encodePacked(
            type(BondAccountFactory).creationCode,
            abi.encode(implementation, entryPoint, protocol)
        );

        address factory = Create2.deploy(0, salt, bytecode);

        emit FactoryDeployed(factory, implementation, protocol, salt);
        return factory;
    }

    // Get factory address before deployment
    function getFactoryAddress(
        address implementation,
        address entryPoint,
        address protocol,
        bytes32 salt
    ) public view returns (address) {
        bytes memory bytecode = abi.encodePacked(
            type(BondAccountFactory).creationCode,
            abi.encode(implementation, entryPoint, protocol)
        );

        return Create2.computeAddress(salt, keccak256(bytecode), address(this));
    }

    // Deploy both implementation and factory in one transaction
    function deployAll(
        address entryPoint,
        address protocol,
        bytes32 implementationSalt,
        bytes32 factorySalt
    ) external returns (address implementation, address factory) {
        // Deploy implementation first
        implementation = deployImplementation(
            entryPoint,
            protocol,
            implementationSalt
        );

        // Deploy factory with the implementation
        factory = deployFactory(
            implementation,
            entryPoint,
            protocol,
            factorySalt
        );

        return (implementation, factory);
    }
}

contracts/interfaces/IBondPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import {DataTypes} from "../libraries/DataTypes.sol";

/**
 * @title IPool
 * @dev Interface of the liquidity pool contract.
 */
interface IBondPool {
    /**
     * @dev Creates a new liquidity pool with the specified parameters, also create the lp token for the pool.
     * @param _poolId Pool ID.
     * @param _underlyingToken Address of the underlying token.
     * @param _supplyTokenName Name of the supply token.
     * @param _supplyTokenSymbol Symbol of the supply token.
     */
    function createPool(
        uint64 _poolId,
        address _underlyingToken,
        string memory _supplyTokenName,
        string memory _supplyTokenSymbol
    ) external returns (DataTypes.PoolData memory _pool);

    /**
     * @dev Allows a user to supply tokens to the pool and receive supply tokens in return.
     * @param _poolId Pool ID.
     * @param _amount Amount of tokens to supply.
     * @param _onBehalfOf The address on behalf of which the tokens are supplied.
     */
    function supply(
        uint64 _poolId,
        uint256 _amount,
        address _onBehalfOf
    ) external;

    /**
     * @dev Allows a user to withdraw tokens from the pool.
     * @param _poolId Pool ID.
     * @param _amount Amount of tokens to withdraw.
     * @param _onBehalfOf The address on behalf of which the tokens are withdrawn.
     */
    function withdraw(
        uint64 _poolId,
        uint256 _amount,
        address _onBehalfOf
    ) external;

    /**
     * @notice Handles the receipt of tokens from the bridge and performs necessary actions.
     * @param _poolId Destination pool ID.
     * @param _fee Fee associated with the transaction.
     */
    function handlePoolIncentive(uint64 _poolId, uint256 _fee) external;

    /**
     * @dev Updates user incentives for a specific pool.
     * @param _user User address.
     * @param _poolId Pool ID.
     */
    function updateUserIncentives(address _user, uint64 _poolId) external;

    /**
     * @dev Retrieves data for a specific pool.
     * @param _poolId Pool ID.
     * @return _pool Pool data structure.
     */
    function getPool(
        uint64 _poolId
    ) external view returns (DataTypes.PoolData memory _pool);

    /**
     * @dev Retrieves user data for a specific account.
     * @param _account User address.
     * @return _user User data structure.
     */
    function getUserData(
        address _account
    ) external view returns (DataTypes.UserData memory _user);
}

contracts/interfaces/IBondProtocol.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import {DataTypes} from "../libraries/DataTypes.sol";

/**
 * @title IBondProtocol
 * @dev Interface of the protocol main contract.
 */
interface IBondProtocol {
    /**
     * @notice This function sends the intent message to every required chain
     * @dev This is the entry function to send an intent request
     * @param _intentData the intent bytes to be executed
     */
    function submitIntent(bytes memory _intentData) external;

    /**
     * @notice This function just simulates the submitIntent function above and get required fees to process the intent passed
     * @dev simulate submiting intent and get fees
     * @param _intentData the intent bytes data
     */
    function getFees(bytes memory _intentData) external;

    /**
     * @notice This function emits event that notifies solvers to build next required userop
     * @dev This function is called by _ccipReceive from the bridge contract, data is to be consumed by the protocol
     * @param _data this is the CCIP bridged message (DataTypes.BridgeData).
     * @param srcBridgedChainSelector The source chain selector from CCIP
     */
    function confirmIncomingIntent(
        bytes memory _data,
        uint64 srcBridgedChainSelector
    ) external;

    /**
     * @dev This function is called by account to settle intent destination chain
     * @param _intentId intent Id to interact with
     * @param _executor this is the solver that built the userOp
     */
    function settleIntentDestChain(
        bytes32 _intentId,
        address _executor
    ) external;

    /**
     * @dev This function is called by account to settle the solver that build the userOp to run the intent destination datas
     * @param _intentId intent Id to interact with
     * @param _executor this is the solver that built the userOp
     */
    function settleIntentDestExecutor(
        bytes32 _intentId,
        address _executor
    ) external;

    /**
     * @dev helper function to check if intent has been used on this chain
     * @param _intentId Intent id to check
     */
    function isIntentExecuted(bytes32 _intentId) external view returns (bool);

    /**
     * @notice Creates a new pool with the specified parameters.
     * @param _poolId Pool ID.
     * @param _underlyingToken Address of the underlying token.
     * @param _supplyTokenName Name of the supply token.
     * @param _supplyTokenSymbol Symbol of the supply token.
     */
    function createPool(
        uint64 _poolId,
        address _underlyingToken,
        string memory _supplyTokenName,
        string memory _supplyTokenSymbol
    ) external returns (DataTypes.PoolData memory);

    /**
     * @notice Retrieves pool data for a given pool ID.
     * @param _poolId Pool ID.
     * @return Pool data structure.
     */
    function getPool(
        uint64 _poolId
    ) external view returns (DataTypes.PoolData memory);

    /**
     * @dev retreive intent data
     * @param _intentId intent Id to be retreived
     */
    function getIntent(
        bytes32 _intentId
    ) external view returns (DataTypes.IntentData memory);

    /**
     * @dev checks if all source chains in intent has settled the destination chain
     * @param _intentId intent Id ro be checked
     */
    function isIntentDstChainFullySettled(
        bytes32 _intentId
    ) external view returns (bool);

    /**
     * @dev helper function to check if intent is valid
     * @param _intentId Intent id to check
     */
    function isIntentValid(
        bytes32 _intentId,
        address sender_
    ) external view returns (bool);

    /**
     * @dev Retreive current intent nonce for sender
     * @param _sender the sender to retreive intent nonce for
     */
    function getNonce(address _sender) external view returns (uint64);

    /**
     * @dev Admin only function to initailize protocol bridge address
     * @param bridge_ bridge address
     */
    function initializeBridge(address bridge_) external;

    /**
     * @dev Admin only function to initailize protocol pool address
     * @param pool_ pool address
     */
    function initializePool(address pool_) external;
}

